
public class CharacterPrinter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int level;
		double health;
		String name;
		
		level = 1;
		System.out.println("Level: "+ level);
		
		health = 12.5;
		health = 9;
		System.out.println("Health: " + health);
		
		name = "Felicia";
		System.out.println("Character Name: " + name);

	}

}

